import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";
import axios from 'axios';
import api from 'axios';




const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjoiVVNFUk5BTUUiLCJpYXQiOjE2NTc4MTQxNjYsImV4cCI6MTY1ODQxODk2Nn0.o5iUKek8nvnKsyskw41p8n6I5XEYkmokBWVJytKr7Wk';
const apiURL = 'http://135.181.35.61:2112/';

// send token with request
axios.interceptors.request.use(
  config => {
    config.headers.authorization = `Bearer ${token} `;
    return config;
  },
  error => {
    return Promise.reject(error);
  }
)


const Info = () => {

  const [blocks, setBlocks] = useState('');
  const [contact, setContact] = useState('');
  const { register, handleSubmit } = useForm();

  useEffect(() => {
    // get company data
    const fetchData = async () => {
      try {
        const response = await api.get(`${apiURL}companies/12`);
        const contactResponse = await api.get(`${apiURL}contacts/16`)
        setBlocks(response.data);
        setContact(contactResponse.data);
      } catch (err) {
        if (err.response) {
          //not in the 200 response range
          console.log(err.response.data);
          console.log(err.response.status);
          console.log(err.response.headers);
        } else {
          console.log(`Error: ${err.message}`);
        }
      }
    }
    fetchData();
  }, []);

  //upload images
const onSubmit =(data) => {
const storageRef = app.storage().ref();
const fileRef = storageRef.child(data.image[0].name);
fileRef.put(data.image[0])
}


  return (
    <>
      <div className="block__info">
        <div className="block__info-title">
          ОБЩАЯ ИНФОРМАЦИЯ
        </div>
        <div className="form">
          <label htmlFor="full-business-name">
            Полное название:
            <input type="text" value={blocks.name} />
          </label>
          <label htmlFor="contract">
            Договор:
            <input type="text" value={`  от ${blocks.contract}`} />
          </label>
          <label htmlFor="entity">
            Форма:
            <input type="text" value={blocks.businessEntity} />
          </label>
          <label htmlFor="type">
            Тип:
            <input type="text" value={blocks.type} />
          </label>
        </div>
      </div>
      <div className="block__info">
        <div className="block__info-title">
          КОНТАКТНЫЕ ДАННЫЕ
        </div>
        <div className="form">
          <label htmlFor="name">
            ФИО:
            <input type="text" value={`${contact.lastname} ${contact.firstname} ${contact.patronymic}`} />
          </label>
          <label htmlFor="phone">
            Телефон:
            <input type="text" value={contact.phone} />
          </label>
          <label htmlFor="email">
            Эл. почта:
            <input type="text" value={contact.email} />
          </label>
        </div>
      </div>
      <div className="block__info">
        <div className="block__info-title">
          ПРИЛОЖЕННЫЕ ФОТО
        </div>
        <div className="images">
          <form onSubmit={handleSubmit(onSubmit)}>
            <input ref={register} name="image" type="file" />
            <button>Submit</button>
          </form>
        </div>
      </div>
    </>


  )
}

export default Info
