import React, { useEffect, useState } from 'react';
import axios from 'axios';
import api from 'axios';
import FileUpload from './FileUpload';
import FileList from './FileList';




const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjoiVVNFUk5BTUUiLCJpYXQiOjE2NTc4MTQxNjYsImV4cCI6MTY1ODQxODk2Nn0.o5iUKek8nvnKsyskw41p8n6I5XEYkmokBWVJytKr7Wk';
const apiURL = 'http://135.181.35.61:2112/';

// send token with request
axios.interceptors.request.use(
  config => {
    config.headers.authorization = `Bearer ${token} `;
    return config;
  },
  error => {
    return Promise.reject(error);
  }
)


const Info = () => {

  const [blocks, setBlocks] = useState('');
  const [contact, setContact] = useState('');
  const [files, setFiles] = useState([]);

  const [details, setDetails] = useState({
    name:
    '',
    phone: "",
    email: "",
  });

  console.log(details)

  useEffect(() => {
    // get company data
    const fetchData = async () => {
      try {
        const response = await api.get(`${apiURL}companies/12`);
        const contactResponse = await api.get(`${apiURL}contacts/16`)
        setBlocks(response.data);
        setContact(contactResponse.data);
      } catch (err) {
        if (err.response) {
          //not in the 200 response range
          console.log(err.response.data);
          console.log(err.response.status);
          console.log(err.response.headers);
        } else {
          console.log(`Error: ${err.message}`);
        }
      }
    }
    fetchData();
  }, []);



  // update contact info
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      // search prev val and update
      return { ...prev, [name]: value }
    });
  }
  // handle contact submit
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(details)
  }

  //upload images
  const removeFile = (filename) => {
    setFiles(files.filter(file => file.name !== filename))
  }


  return (
    <>
      {/* <div className="block__info">
        <div className="block__info-title">
          ОБЩАЯ ИНФОРМАЦИЯ
        </div>
        <div className="form">
          <label htmlFor="full-business-name">
            Полное название:
            <input type="text" value={blocks.name} />
          </label>
          <label htmlFor="contract">
            Договор:
            <input type="text" value={`  от ${blocks.contract}`} />
          </label>
          <label htmlFor="entity">
            Форма:
            <input type="text" value={blocks.businessEntity} />
          </label>
          <label htmlFor="type">
            Тип:
            <input type="text" value={blocks.type} />
          </label>
        </div>
      </div> */}
      <div className="block__info">
        <div className="block__info-title">
          КОНТАКТНЫЕ ДАННЫЕ
        </div>
        <form className="form" onSubmit={handleSubmit}>
          <label htmlFor="name">
            ФИО:
            <input
              type="text"
              name="name"
              value={`${contact.lastname} ${contact.firstname} ${contact.patronymic}`}
              onChange={handleChange} />
          </label>
          <label htmlFor="phone">
            Телефон:
            <input type="text"
              value={contact.phone}
              name="phone"
              onChange={handleChange} />
          </label>
          <label htmlFor="email">
            Эл. почта:
            <input type="text"
              value={contact.email}
              name="email"
              onChange={handleChange} />
          </label>
          <input type="submit" value="Сохранить" />
        </form>
      </div>
      <div className="block__info">
        <div className="block__info-title">
          ПРИЛОЖЕННЫЕ ФОТО
        </div>
        <div className="images">
          <div className="title">Upload file</div>
          <FileUpload files={files} setFiles={setFiles}
            removeFile={removeFile} />
          <FileList files={files} removeFile={removeFile} />
        </div>
      </div>
    </>


  )
}

export default Info
